/**
 *  项目的工具库
 */
module.exports = {
    /**
     *  Merge two object, form dest to tar
     */
    merge: function (tar, dest, overwrite) {
        overwrite = overwrite && overwrite !== undefined
        for (var k in dest) {
            if (!dest.hasOwnProperty(k) || 
                ( !overwrite && tar.hasOwnProperty(k)) ) {
                continue
            }
            tar[k] = dest[k]
        }
        return tar
    },
    slice: function (arr) {
        return Array.prototype.slice.call(arr)
    },
    toString: function (obj) {
        return Object.prototype.toString.call(obj)
    },
    type: function(obj) {
        return this.toString.call(obj).match(/\[object (\w+)\]/)[1].toLowerCase()
    }
}
