describe('#Error', function() {

    var Base = napi.base()
    var News = napi.object('news')


    describe('[Not Found]', function() {
        it('Get a not exist object', function(done) {
            Base
                .send({
                    method: 'GET',
                    url: '/classes/news/objects/kevinwhoissb'
                })
                .done(function(data) {
                    expect(false, 'Should done with error')
                    done()
                }, function(err) {
                    expect(err).to.be.an('object')
                    expect(err).to.include.keys('message', 'code', 'type', 'statusCode')
                    expect(err.type).to.equal('api')
                    expect(err.statusCode).to.equal(404)
                    done()
                })
        })
        it('Get a not exist list', function(done) {
            News
                .list('kevinwhoissb')
                .done(function(data) {
                    expect(false, 'Should done with error')
                    done()
                }, function(err) {
                    expect(err).to.be.an('object')
                    expect(err).to.include.keys('message', 'code', 'type', 'statusCode')
                    expect(err.type).to.equal('api')
                    expect(err.statusCode).to.equal(404)
                    done()
                })
        })
    })
})